$cleartempfiles.Add_Click({
    Log("Clearing Temporary Files....")
    Start-Sleep -Seconds (5)
    taskkill /F /IM explorer.exe
    Remove-Item -Path $env:TEMP\* -Force -Recurse
    Remove-Item -Path C:\Temp -Recurse
    Remove-Item -Path %temp% -Recurse
    Remove-Item -Path C:\Users\Isaia\AppData\Local\FortniteGame\Saved\Config\CrashReportClient\CrashReportClient.ini -Recurse
    ipconfig /flushdns
    Remove-Item -Path *.log /a /s /q /f -Force -Recurse
    Remove-Item -Path /f "%temp%" /r /d y -Force -Recurse
    Remove-Item -Path C:\windows\temp. -Recurse
    Remove-Item -Path C:\WINDOWS\Prefetch -Recurse
    Remove-Item -Path %systemdrive%\*.tmp -Recurse
    Remove-Item -Path %systemdrive%\*._mp -Recurse
    Remove-Item -Path %systemdrive%\*.log -Recurse
    Remove-Item -Path %systemdrive%\*.gid -Recurse
    Remove-Item -Path %systemdrive%\*.chk -Recurse
    Remove-Item -Path %systemdrive%\*.old -Recurse
    Remove-Item -Path %systemdrive%\recycled\*.* -Recurse
    Remove-Item -Path %systemdrive%\$Recycle.Bin\*.* -Recurse
    Remove-Item -Path %windir%\*.bak -Recurse
    Remove-Item -Path %windir%\prefetch\*.* -Recurse
    Remove-Item -Path %LocalAppData%\Microsoft\Windows\Explorer\thumbcache_*.db -Recurse
    Remove-Item -Path %LocalAppData%\Microsoft\Windows\Explorer\*.db -Recurse
    Remove-Item -Path %SystemRoot%\Logs\CBS\CBS.log -Recurse
    Remove-Item -Path %SystemRoot%\Logs\DISM\DISM.log -Recurse
    Remove-Item -Path C:\Windows\Temp -Recurse
    Remove-Item -Path c:\windows\tempor~1 -Recurse
    Remove-Item -Path c:\windows\temp -Recurse
    Remove-Item -Path c:\windows\tmp -Recurse
    Remove-Item -Path c:\windows\ff*.tmp -Recurse
    Remove-Item -Path c:\windows\history -Recurse
    Remove-Item -Path c:\windows\cookies -Recurse
    Remove-Item -Path c:\windows\recent -Recurse
    Remove-Item -Path c:\windows\spool\printers -Recurse
    $Devices = Get-PnpDevice | ? Status -eq Unknown;foreach ($Device in $Devices) { &\"pnputil\" /remove-device $Device.InstanceId }
    Clear-RecycleBin -Force
    start explorer.exe
    Log("Successfully Cleared Temp Files!")
})

