$usbtweaks.Add_Click({
    Log("Disabling USB Power Savings....")
    Start-Sleep -Seconds (5)
    Set-ItemProperty -Path "HKLM\SYSTEM\CurrentControlSet\Enum\%%i\Device Parameters" -Name "AllowIdleIrpInD3" -Type DWord -Value 0
